if (exist('features.mat', 'file') == 2) && (exist('targets.mat', 'file') == 2) && (exist('spatial_transform.mat', 'file') == 2) && (exist('temporal_transform.mat', 'file') == 2)fprintf('Features have already been extracted before ... loading from file');X_train = load('features.mat');Y_train = load('targets.mat');S = load('spatial_transform.mat');T = load('temporal_transform.mat');X_train = X_train.X_train;Y_train = Y_train.Y_train;S = S.S;T = T.T;else fprintf('Data being loaded and features extracted for the first time and saved to file');load data_set_IVb_al_train%Bandpass filter between 7 and 30 Hzflt = @(f)(f>7&f<30).*(1-cos((f-(7+30)/2)/(7-30)*pi*4));  [X_train,Y_train, S, T] = extract_csp_features(single(cnt), nfo.fs, sparse(1,mrk.pos,(mrk.y+3)/2),[0.5 3.5],flt,3,200);  % Write pre-processed data to filesave('features.mat', 'X_train')save('targets.mat', 'Y_train')save('spatial_transform.mat', 'S')save('temporal_transform.mat', 'T')end 